DROP TABLE IF EXISTS `country_info`;

CREATE TABLE IF NOT EXISTS `country_info` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `iso_code_2` varchar(2) NOT NULL COMMENT 'ISO 3166-1 二字母代码',
  `iso_code_3` varchar(3) NOT NULL COMMENT 'ISO 3166-1 三字母代码',
  `iso_code_numeric` varchar(3) NOT NULL COMMENT 'ISO 3166-1 数字代码',
  `name_en` varchar(100) NOT NULL COMMENT '国家/地区英文名称',
  `name_zh` varchar(100) NOT NULL COMMENT '国家/地区中文名称',
  `sovereignty` varchar(50) DEFAULT NULL COMMENT '主权状态',
  `continent` varchar(20) NOT NULL COMMENT '所属大洲',
  `currency_code` varchar(3) DEFAULT NULL COMMENT 'ISO 4217 货币代码',
  `phone_code` varchar(10) DEFAULT NULL COMMENT '国际电话区号',
  `timezone_iana` varchar(50) DEFAULT NULL COMMENT '主要 IANA 时区',
  PRIMARY KEY (`id`),
  UNIQUE KEY `iso_code_2` (`iso_code_2`),
  UNIQUE KEY `iso_code_3` (`iso_code_3`)
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = DYNAMIC COMMENT = '全球国家和地区信息';


